    Iterator it = selectedKeys.iterator();
    //éå†å¯è¯»å†™çš„FD
    while (it.hasNext()) {
        SelectionKey key = it.next();
        // åˆ¤æ–­ç±»å‹æ˜¯å¯æ¥æ”¶ã€å¯è¯»ã€å¯å†™ï¼Œå¯è¿æ¥ï¼ˆè¿æ¥å¦ä¸€ä¸ªæœåŠ¡ï¼‰
        if (key.isAcceptable()) {
