            writeBuff.rewind();
            SocketChannel socketChannel = (SocketChannel) key.channel();
            // å°†æ•°æ®ä»ç¼“å†²åŒºå†™å…¥Channel
            socketChannel.write(writeBuff);
             // å†™æ“ä½œå®Œæ¯•ï¼Œè®¾ç½®ä¸ºå¯è¯»
            key.interestOps(SelectionKey.OP_READ);
        }
        it.remove();
    }
}

