public class PULLXml {
    public static List pull2xml(InputStream is) throws Exception {
        List list = null;
        Student student = null;
        //åˆ›å»ºxmlPullè§£æå™¨
        XmlPullParserFactory.newInstance().newPullParser();
        XmlPullParser parser = Xml.newPullParser();
        ///åˆå§‹åŒ–xmlPullè§£æå™¨
        parser.setInput(is, "utf-8");
        //è¯»å–æ–‡ä»¶çš„ç±»å‹
        int type = parser.getEventType();
        //å¾ªç¯åˆ¤æ–­äº‹ä»¶ç±»å‹ï¼Œç›´åˆ°documentç»“æŸ
        while (type != XmlPullParser.END_DOCUMENT) {
            switch (type) {
                //å¼€å§‹æ ‡ç­¾
                case XmlPullParser.START_TAG:
                    if ("students".equals(parser.getName())) {
                        list = new ArrayList<>();
                    } else if ("student".equals(parser.getName())) {
                        student = new Student();
                    } else if ("name".equals(parser.getName())) {
                        //è·å–sexå±æ€§
                        String sex = parser.getAttributeValue(null,"sex");
                        student.setSex(sex);
                        //è·å–nameå€¼
                        String name = parser.nextText();
                        student.setName(name);
                    } else if ("nickName".equals(parser.getName())) {
                        //è·å–nickNameå€¼
                        String nickName = parser.nextText();
                        student.setNickName(nickName);
                    }
                    break;
                //ç»“æŸæ ‡ç­¾
                case XmlPullParser.END_TAG:
                    if ("student".equals(parser.getName())) {
                        list.add(student);
                    }
                    break;
            }
            //ç»§ç»­å¾€ä¸‹è¯»å–æ ‡ç­¾ç±»å‹
            type = parser.next();
        }
        return list;
    }
}

